$map: ();
@each $name, $value in $sizes {
  $keyName: $name;
  @if ($char != '' and $keyName != null) {
    $keyName: $char + $keyName;
  }
  $keyValue: $value * $bolt-spacing-gutter;
  $map: map-merge($map, ($keyName: $keyValue));
}
@return $map;

